                                "type": "string"
                            },
                            "pincode": {
                                "type": "string"
                            },
                            "addressName": {
                                "type": "string"
                            },
                            "userDetailsDto": {
                                "type": "object",
                                "properties": {
                                    "name": {
                                        "type": "string"
                                    },
                                    "email": {
                                        "type": "string"
                                    },
                                    "primaryPhoneNo": {
                                        "type": "string"
                                    },
                                    "secondaryPhoneNo": {
                                        "type": ["string", "null"]
                                    },
